/**
 * Windshaft client. It provides a method to create instances of dashboards.
 * @param {object} options Options to set up the client
 */
cdb.windshaft.Client = function(options) {
  this.ajax = window.$ ? window.$.ajax : reqwest.compat;
  this.windshaftURLTemplate = options.windshaftURLTemplate;
  this.userName = options.userName;
  this.url = this.windshaftURLTemplate.replace('{user}', this.userName);
  this.statTag = options.statTag;
  this.isCorsSupported = cdb.core.util.isCORSSupported();
  this.forceCors = options.forceCors;
  this.endpoint = options.endpoint;
}

cdb.windshaft.Client.DEFAULT_COMPRESSION_LEVEL = 3;
cdb.windshaft.Client.MAX_GET_SIZE = 2033;

/**
 * Creates an instance of a map in Windshaft
 * @param {object} mapDefinition An object that responds to .toJSON with the definition of the map
 * @param  {function} callback A callback that will get the public or private map
 * @return {cdb.windshaft.DashboardInstance} The instance of the dashboard
 */
cdb.windshaft.Client.prototype.instantiateMap = function(mapDefinition) {
  var payload = JSON.stringify(mapDefinition);

  var dashboardInstance = new cdb.windshaft.DashboardInstance();

  var options = {
    success: function(data) {
      if (data.errors) {
        // TODO: Error handling
        throw data.errors;
      } else {
        data.windshaftURLTemplate = this.windshaftURLTemplate;
        data.userName = this.userName;
        dashboardInstance.set(data);
      }
    }.bind(this),
    error: function(xhr) {
      var err = { errors: ['Unknown error'] };
      try {
        err = JSON.parse(xhr.responseText);
      } catch(e) {}
      throw err.errors;
    }
  }

  if (this._usePOST(payload)) {
    this._post(payload, options);
  } else {
    this._get(payload, options);
  }

  return dashboardInstance;
}

cdb.windshaft.Client.prototype._usePOST = function(payload) {
  if (this.isCorsSupported && this.forceCors) {
    return true;
  }
  return payload.length >= this.constructor.MAX_GET_SIZE;
}

cdb.windshaft.Client.prototype._post = function(payload, options) {
  this.ajax({
    crossOrigin: true,
    type: 'POST',
    method: 'POST',
    dataType: 'json',
    contentType: 'application/json',
    url: this._getURL(),
    data: payload,
    success: options.success,
    error: options.error
  });
}

cdb.windshaft.Client.prototype._get = function(payload, options) {
  var compressFunction = this._getCompressor(payload);
  compressFunction(payload, this.constructor.DEFAULT_COMPRESSION_LEVEL, function(dataParameter) {
    this.ajax({
      url: this._getURL(dataParameter),
      dataType: 'jsonp',
      jsonpCallback: this._jsonpCallbackName(payload),
      cache: true,
      success: options.success,
      error: options.error
    });
  }.bind(this));
}

cdb.windshaft.Client.prototype._getCompressor = function(payload) {
  if (payload.length < this.constructor.MAX_GET_SIZE) {
    return function(data, level, callback) {
      callback("config=" + encodeURIComponent(data));
    };
  }

  return function(data, level, callback) {
    data = JSON.stringify({ config: data });
    LZMA.compress(data, level, function(encoded) {
      callback("lzma=" + encodeURIComponent(cdb.core.util.array2hex(encoded)));
    });
  };
}


cdb.windshaft.Client.prototype._getURL = function(dataParameter) {
  var params = [];
  params.push(["stat_tag", this.statTag].join("="));
  if (dataParameter) {
    params.push(dataParameter);  
  }
  return [this.url, this.endpoint].join('/') + '?' + params.join('&');
}

cdb.windshaft.Client.prototype._jsonpCallbackName = function(payload) {
  return '_cdbc_' + cdb.core.util.uniqueCallbackName(payload);
}
cdb.windshaft.Dashboard = function(options) {
  this.cartoDBLayerGroup = options.cartoDBLayerGroup;
  this.widgets = options.widgets;
  this.client = options.client;
  this.statTag = options.statTag;
  this.configGenerator = options.configGenerator;

  this.instance = new cdb.windshaft.DashboardInstance();
  this.cartoDBLayerGroup.bindDashboardInstance(this.instance);

  // Bindings
  this.cartoDBLayerGroup.layers.bind('change', this.createInstance, this);
  this.instance.bind('change:layergroupid', this._updateWidgets, this);
};

cdb.windshaft.Dashboard.prototype._updateWidgets = function(dashboardInstance) {
  _.each(this.widgets, function(widget) {
    widget.set({
      dashboardBaseURL: dashboardInstance.getBaseURL(),
    });
  }.bind(this));
};

cdb.windshaft.Dashboard.prototype.createInstance = function() {
  var dashboardConfig = this.configGenerator.generate(this);
  var instance = this.client.instantiateMap(dashboardConfig);
  instance.bind('change:layergroupid', function() {
    this.instance.set(instance.toJSON());
  }.bind(this));

  return this.instance;
};

cdb.windshaft.Dashboard.prototype.getLayers = function() {
  return this.cartoDBLayerGroup.layers;
};

cdb.windshaft.Dashboard.prototype.getVisibleLayers = function() {
  return this.cartoDBLayerGroup.getVisibleLayers();
};

cdb.windshaft.Dashboard.prototype.getWidgets = function() {
  return this.widgets;
};

cdb.windshaft.DashboardInstance = cdb.core.Model.extend({
 
  initialize: function() {
    this.ajax = $.ajax;

    // TODO: What params are really used?
    this.pngParams = ['map_key', 'api_key', 'cache_policy', 'updated_at'];
    this.gridParams = ['map_key', 'api_key', 'cache_policy', 'updated_at'];
  },

  getMapId: function() {
    return this.get('layergroupid');
  },

  getBaseURL: function() {
    return [
      this.getHost(),
      cdb.windshaft.config.MAPS_API_BASE_URL,
      this.getMapId(),
    ].join('/');
  },

  getHost: function(subhost) {
    var userName = this.get('userName');
    var protocol = this._useHTTPS() ? 'https' : 'http';
    var subhost = subhost || '';
    var host = this.get('windshaftURLTemplate').replace('{user}', userName);
    var cdnHost = this.get('cdn_url') && this.get('cdn_url')[protocol];
    if (cdnHost) {
      host = [protocol, '://', subhost, cdnHost, '/', userName].join('');
    }
    
    return host;
  },

  _useHTTPS: function() {
    return this.get('windshaftURLTemplate').indexOf('https') === 0;
  },

  /**
   * Returns the index of a layer of a given type, as the tiler kwows it.
   *
   * @param {integer} index - number of layer of the specified type
   * @param {string} layerType - type of the layers
   */
  getLayerIndexByType: function(index, layerType) {
    var layers = this.get('metadata') && this.get('metadata').layers;

    if (!layers) {
      return index;
    }

    var tilerLayerIndex = {};
    var j = 0;
    for (var i = 0; i < layers.length; i++) {
      if (layers[i].type == layerType) {
        tilerLayerIndex[j] = i;
        j++;
      }
    }
    if (tilerLayerIndex[index] === undefined) {
      return -1;
    }
    return tilerLayerIndex[index];
  },

  /**
   * Returns the index of a layer of a given type, as the tiler kwows it.
   *
   * @param {string|array} types - Type or types of layers
   */
  getLayerIndexesByType: function(types) {
    var layers = this.get('metadata') && this.get('metadata').layers;

    if (!layers) {
      return;
    }
    var layerIndexes = [];
    for (var i = 0; i < layers.length; i++) {
      var layer = layers[i];
      var isValidType = layer.type !== 'torque';
      if (types && types.length > 0) {
        isValidType = isValidType && types.indexOf(layer.type) != -1;
      }
      if (isValidType) {
        layerIndexes.push(i);
      }
    }
    return layerIndexes;
  },

  getTiles: function() {
    var grids = [];
    var tiles = [];
    var params = [];
    var pngParams = this._encodeParams(params, this.pngParams);
    var gridParams = this._encodeParams(params, this.gridParams);
    var subdomains = ['0', '1', '2', '3'];

    if(this._useHTTPS()) {
      subdomains = [''];
    }

    var layerIndexes = this.getLayerIndexesByType("mapnik");
    if (layerIndexes.length) {
      var tileTemplate = '/' +  layerIndexes.join(',') +'/{z}/{x}/{y}';
      var gridTemplate = '/{z}/{x}/{y}';

      for(var i = 0; i < subdomains.length; ++i) {
        var s = subdomains[i];
        var cartodb_url = this.getHost(s) + MapBase.BASE_URL + '/' + this.getMapId();
        tiles.push(cartodb_url + tileTemplate + ".png" + (pngParams ? "?" + pngParams: '') );

        for(var layer = 0; layer < this.get('metadata').layers.length; ++layer) {
          var index = this.getLayerIndexByType(layer, "mapnik");
          grids[layer] = grids[layer] || [];
          grids[layer].push(cartodb_url + "/" + index +  gridTemplate + ".grid.json" + (gridParams ? "?" + gridParams: ''));
        }
      }
    } else {
      tiles = [MapBase.EMPTY_GIF];
    }

    this.urls = {
      tiles: tiles,
      grids: grids
    };
    return this.urls;
  },

  _encodeParams: function(params, included) {
    if(!params) return '';
    var url_params = [];
    included = included || _.keys(params);
    for(var i in included) {
      var k = included[i];
      var p = params[k];
      if(p) {
        if (_.isArray(p)) {
          for (var j = 0, len = p.length; j < len; j++) {
            url_params.push(k + "[]=" + encodeURIComponent(p[j]));
          }
        } else {
          var q = encodeURIComponent(p);
          q = q.replace(/%7Bx%7D/g,"{x}").replace(/%7By%7D/g,"{y}").replace(/%7Bz%7D/g,"{z}");
          url_params.push(k + "=" + q);
        }
      }
    }
    return url_params.join('&');
  },

  fetchAttributes: function(layer, featureID, callback) {
    var url = [
      this.getBaseURL(),
      this.getLayerIndexByType(layer, "mapnik"),
      'attributes',
      featureID
    ].join('/');

    $.ajax({
      dataType: 'jsonp',
      url: url,
      jsonpCallback: '_cdbi_layer_attributes_' + cdb.core.util.uniqueCallbackName(this.toJSON()),
      cache: true,
      success: function(data) {
        // loadingTime.end();
        callback(data);
      },
      error: function(data) {
        // loadingTime.end();
        // cartodb.core.Profiler.metric('cartodb-js.named_map.attributes.error').inc();
        callback(null);
      }
    });
  }
})

